<?php/*Plugin Name: FAF ComparePlugin URI: http://fafworx.deDescription: Show images before/after comparison of your WordPress images (based on TwentyTwenty).Version: 1.0Author: F.A. FiebigAuthor URI: http://fafworx.deLicense: GNU GENERAL PUBLIC LICENSE*/defined( 'ABSPATH' ) or die( 'No direct script access allowed!' );/** * add scripts and styles */function faf_compare_scripts_styles(){    wp_enqueue_script('event-move-js', plugins_url('twentytwenty/js/jquery.event.move.js', __FILE__), array('jquery'), '1.0', true);    wp_enqueue_script('twentytwenty-js', plugins_url('twentytwenty/js/jquery.twentytwenty.js', __FILE__), array('jquery'), '1.0', true);    wp_enqueue_script('bc-co-js', plugins_url('twentytwenty/js/jquery.compare.js', __FILE__), array('jquery'), '1.0', true);    wp_enqueue_style('foundation-css', plugins_url('twentytwenty/css/foundation.css', __FILE__));    wp_enqueue_style('twentytwenty-css', plugins_url('twentytwenty/css/twentytwenty.css', __FILE__), array('foundation-css'));}add_action('wp_enqueue_scripts', 'faf_compare_scripts_styles');/** * add scripts and styles to admin */function faf_compare_admin_scripts_styles(){    wp_enqueue_script('faf-compare-editor-js', plugins_url('editor/editor.js', __FILE__), array('jquery'), '1.0', true);}add_action('admin_enqueue_scripts', 'faf_compare_admin_scripts_styles');/** * add editor button */function faf_compare_add_media_button(){    echo '<a href="#" id="faf-compare-short" class="button">'.faf_compare_trans('FAF Compare').'</a>';}add_action('media_buttons', 'faf_compare_add_media_button', 101);/** * add short code * * @param $atts * @return string */function faf_compare( $atts ){    if (isset($atts['before']) && isset($atts['after'])) {        $orientation    = (isset($atts['orientation'])) ? $atts['orientation'] : 'horizontal';        $size           = (isset($atts['size'])) ? $atts['size'] : 'thumbnail';        $before         = $atts['before'];        $after          = $atts['after'];        $html  = '<div class="twentytwenty-container"  data-orientation="'.$orientation.'">';        $html .= wp_get_attachment_image($before, $size);        $html .= wp_get_attachment_image($after, $size);        $html .= '</div>';        return $html;    }}add_shortcode('compare', 'faf_compare');/** * custom translation */function faf_compare_trans($string){    $locale = get_locale();    $lang = (!empty($locale)) ? $locale : WPLANG;    $transFile = dirname(__FILE__).'/lang/'.$lang.'.php';    if (is_file($transFile)) {        $translation = include($transFile);        if (array_key_exists($string, $translation)) {            return $translation[$string];        }    }    return $string;}